CREATE TABLE `inquiry` (
  `inquiry_id` bigint(20) NOT NULL auto_increment,
  `real_name` varchar(100) default NULL,
  `email` varchar(100) default NULL,
  `hang_ye` varchar(200) default NULL,
  `phone` varchar(100) default NULL,
  `country` varchar(100) default NULL,
  `company` varchar(255) default NULL,
  `info` text,
  `re_info` text,
  `lang` varchar(50) default NULL,
  `other` text,
  `pay_flag` int(11) default NULL,
  `int_flag` int(11) default NULL,
  `flag` tinyint(2) NOT NULL,
  `udate` varchar(50) default NULL,
  `order_flag` tinyint(2) NOT NULL,
  PRIMARY KEY  (`inquiry_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=11 ;

