INSERT INTO `inquiry` VALUES (9, '無懂', 'wudone@gmail.com', NULL, '0919357169', '', '', '林存裕總經理您好~\r\n8/13 週二下午2~4點左右\r\n我們有兩人想親自登門貴公司 \r\n洽談"心理證照+輔導課程"的學習事宜\r\n是否方便呢? 謝謝~', NULL, NULL, NULL, 1, 1, 0, '2013-08-09', 0);
INSERT INTO `inquiry` VALUES (10, 'Janet', 'janetccs@gmail.com', NULL, '85297723421', '', '', '對取得中醫博士很感興趣，但我本身是香港人，已完成香港的中醫碩士課程，在香港有工作，無辦法每星期來台灣上課 。所以，想問一下：\r\n1. 能否在香港透過上電腦網絡（internet)/DVD，遠距離地上課 ，都可以取得博士學位？\r\n2. 有沒有博士課程，密集式的，一年只需親臨台灣/大陸/美國兩次，便可取得博士學位？\r\n\r\n謝謝您 。', NULL, NULL, NULL, 1, 1, 0, '2013-11-12', 0);

