INSERT INTO `study` VALUES (16, 'study_info.php', NULL, '台灣學歷認證', '', '<strong>申請中國學歷認證需繳交證明文件檢核表：</strong><br />\r\n<br />\r\n<br />\r\n一、申請人台胞證正本。<br />\r\n二、學歷證書正本。<br />\r\n三、學習成績單正本。<br />\r\n四、二吋半身照片一張(藍底、無帽)。<br />\r\n五、身分證正本或個人戶籍正本一份。<br />\r\n六、學歷認證申請書一份。<br />\r\n七、上述所有資料之影印本一份。<br />\r\n八、授權書一份。<br />\r\n<br />\r\n上述學歷認證參加中國律師考試、中國醫師資格考試、中國EMBA考試，中國專利師考試皆可使用。證認辦理時間約3-5週，終身有效。', '', 0, '', '', 1, 10, '2012/05/08', '13359818971188.jpg', 0, '', '');
