INSERT INTO `study` VALUES (32, 'study_info.php', NULL, '現在就訂閱我們的電子報', '', '<!-- BEGIN: Signup Form Button Code from Benchmark Email Ver 2.0 ------>\r\n\r\n<div align="center">\r\n<a href="http://lb.benchmarkemail.com//listbuilder/signupnew?QlB0kxWkuqHDDkVtUfgJ1XTVLJ38FkWyQ0gE57VBLrFcCKnj5b1ICvJh9pUeouFs" \r\n target=_new><img src="http://ui.benchmarkemail.com/images/tw/listbuilder/s5.gif" border=0 /></a>\r\n</div>\r\n\r\n<!-- END: Signup Form Button Code from Benchmark Email Ver 2.0 ---------->\r\n<!-- BEGIN: Email Marketing By Benchmark Email ------>\r\n\r\n<div align="center" style="padding-top:5px;font-family:Arial,Helvetica,sans-serif;font-size:10px;color:#999999;">\r\n<a href="http://www.benchmarkemail.com" target=_new style="text-decoration:none;font-family:Arial,Helvetica,sans-serif;font-size:10px;color:#999999;">Email Marketing Services</a> by Benchmark</div>\r\n\r\n<!-- END: Email Marketing By Benchmark Email ---------->', '', 0, '', '', 1, 1, '2012/12/01', '', 0, '', '');
INSERT INTO `study` VALUES (33, 'study_info.php', NULL, '法律碩士在職課程介紹', '', '<div style="text-align: center;">\r\n <span style="font-size:14px;">中國名校律碩士在職班簡介</span></div>\r\n<br />\r\n<table border="1" cellpadding="0" cellspacing="0">\r\n <tbody>\r\n   <tr>\r\n      <td style="width:111px;">\r\n       <span style="font-size:14px;">項目</span></td>\r\n      <td colspan="2" style="width:457px;">\r\n       <span style="font-size:14px;">法學院：民商法學研究所（知識產權）碩士</span></td>\r\n   </tr>\r\n   <tr>\r\n      <td style="width:111px;">\r\n       <span style="font-size:14px;">排名</span></td>\r\n      <td colspan="2" style="width:457px;">\r\n       <span style="font-size:14px;">校排名全國前十名，是985及211重點大學之一</span></td>\r\n   </tr>\r\n   <tr>\r\n      <td style="width:111px;">\r\n       <span style="font-size:14px;">修業時間</span></td>\r\n      <td colspan="2" style="width:457px;">\r\n       <span style="font-size:14px;">2年－4年</span></td>\r\n   </tr>\r\n   <tr>\r\n      <td style="width:111px;">\r\n       <span style="font-size:14px;">上課模式</span></td>\r\n      <td colspan="2" style="width:457px;">\r\n       <span style="font-size:14px;"><strong>在職碩士每年級中上課2-3次，可依照上課進度調整，依照學校對在職班的規定，必須完成48天左右的課程。</strong></span></td>\r\n   </tr>\r\n   <tr>\r\n      <td style="width:111px;">\r\n       <span style="font-size:14px;">讀書會</span></td>\r\n     <td colspan="2" style="width:457px;">\r\n       <span style="font-size:14px;">2013年12月-2014年2月，每月舉行讀書會一次，以初步理解應考學科的基礎知識</span></td>\r\n   </tr>\r\n   <tr>\r\n      <td style="width:111px;">\r\n       <span style="font-size:14px;">輔導及面試</span></td>\r\n     <td colspan="2" style="width:457px;">\r\n       <span style="font-size:14px;">2014年3月中旬，共需5天（含週六、週日）</span></td>\r\n    </tr>\r\n   <tr>\r\n      <td rowspan="2" style="width:111px;height:70px;">\r\n       <span style="font-size:14px;">考試</span></td>\r\n      <td style="width:34px;height:70px;">\r\n        <span style="font-size:14px;">科目</span></td>\r\n      <td style="width:423px;height:70px;">\r\n       <span style="font-size:14px;">法學碩士（民商法學碩士）：英語、法學綜合（法理學、憲法、刑法、民法）、民商法（知識產權法、民法、商法）。</span></td>\r\n    </tr>\r\n   <tr>\r\n      <td style="width:34px;height:28px;">\r\n        <span style="font-size:14px;">地點</span></td>\r\n      <td style="width:423px;height:28px;">\r\n       <span style="font-size:14px;">廣州或北京</span></td>\r\n   </tr>\r\n   <tr>\r\n      <td style="width:111px;">\r\n       <span style="font-size:14px;">上課安排</span></td>\r\n      <td colspan="2" style="width:457px;">\r\n       <span style="font-size:14px;">36學分（非法律系畢業、攻讀民商法學碩士者須另補修憲法、民法、刑法、法理學等4科），學分課程2年結束（專班密集上課每次8天，總計六次）。含註冊、（行政管理碩士6萬字以上、民商法學碩士7萬字以上）。</span></td>\r\n    </tr>\r\n   <tr>\r\n      <td style="width:111px;">\r\n       <span style="font-size:14px;">招生名額</span></td>\r\n      <td colspan="2" style="width:457px;">\r\n       <span style="font-size:14px;">通過港澳臺研究生入學考試、完成學分、論文開題、論文答辯後可獲頒中國教育部城認的學歷證書與學位證書。</span></td>\r\n   </tr>\r\n   <tr>\r\n      <td style="width:111px;">\r\n       <span style="font-size:14px;">報考資格</span></td>\r\n      <td colspan="2" style="width:457px;">\r\n       <span style="font-size:14px;">學士畢業，不限科系報考</span></td>\r\n   </tr>\r\n </tbody>\r\n</table>\r\n<br />', '', 0, '', '', 0, 23, '2013/11/18', '13847734391927.jpg', 0, '', '');

