CREATE TABLE `xml_output` (
  `id` bigint(20) NOT NULL auto_increment,
  `ctid` varchar(30) NOT NULL,
  `news_category_id` bigint(20) default NULL,
  `title` varchar(255) default NULL,
  `short_des` varchar(255) default NULL,
  `des` text,
  `url_link` varchar(255) default NULL,
  `brows` int(11) default NULL,
  `uptime` varchar(50) default NULL,
  `downtime` varchar(50) default NULL,
  `upordown` int(11) default NULL,
  `indexNo` int(11) default NULL,
  `udate` varchar(50) default NULL,
  `pic` varchar(100) NOT NULL,
  `be_top` bigint(20) NOT NULL,
  `duration` varchar(10) NOT NULL,
  `zoompercent` varchar(10) NOT NULL,
  `effect` varchar(10) NOT NULL,
  `direction` varchar(10) NOT NULL,
  `hzoompos` varchar(10) NOT NULL,
  `vzoompos` varchar(10) NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=14 ;

